import React from 'react'
import Lnav from '../components/lnav'
import './login.css'
import { Link } from 'react-router-dom'
import { useState } from 'react'

export default function Login() {
  const [mail,setMail]=useState('');
  const [password,setPassword]=useState('');


    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:3002/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                console.log(data.message);
            } else {
                console.error(data.error); 
            }
        } catch (error) {
            console.error('An unexpected error occurred:', error);
        }
    };


  return (
    <html>
      <body>
        <div>
          <Lnav/>
          <div className='flex justify-center content-center mt-60'>
            <div className='w-96 h-96 flex justify-center items-center bg-black rounded-2xl'>
                <form>
                  <p className='text-white text-2xl mb-2'>Enter Email id</p>
                  <input type='text' onChange={(e)=>setMail(e.target.value)} className='bg-red-500 h-10 w-64 font-bold text-white text-center rounded-md' placeholder='mail id'></input>

                  <p className='text-white text-2xl mt-6 mb-2'>Enter Password</p>
                  <input type='password' onChange={(e)=>setPassword(e.target.value)} className='bg-red-500 h-10 w-64 font-bold text-white text-center rounded-md' placeholder='password'></input>
                  <hr></hr>
                  <button type='submit' className='text-white font-bold text-xl h-12 w-20 rounded-lg bg-red-500 hover:bg-red-400 hover:text-black mt-10 translate-x-20'>Login</button>

                  <p className='text-white flex justify-center mt-5 gap-2'>New user??<Link to='/signin' className='text-sky-400'>Create an account</Link></p>
                </form>
            </div>
          </div>
        </div>
      </body>
    </html>
  )
}
