import React from 'react'
import Navi from '../components/anav'
import cc from '../images/cc.png'
import { useState,useEffect } from 'react';
import plus from '../images/plus.png'
import minus from '../images/minus.png'
import pp from '../images/pp.jpg';
import rr from '../images/rr.jpg'
import './menu.css'
import axios from 'axios'

export default function Menu({userData}) {
  const [cartCounts, setCartCounts] = useState([0, 0, 0, 0, 0, 0]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Simulate user authentication and data retrieval
    const loginUser = async () => {
      try {
        const response = await axios.post('http://localhost:3002/login', {
          username: 'your_username', // Provide the actual username and password
          password: 'your_password',
        });

        console.log(response.data.message);

        if (response.data.message === 'Login Successful') {
          setUserData(response.data.userData);
        } else {
          setError('Invalid email or password');
        }
      } catch (error) {
        console.error('An unexpected error occurred:', error.message);
        setError('Invalid Credentials');
      }
    };

    loginUser();
  }, []); // This effect runs once on component mount

  const handleRemoveFromCart = (index) => {
    if (cartCounts[index] > 0) {
      const newCartCounts = [...cartCounts];
      newCartCounts[index] -= 1;
      setCartCounts(newCartCounts);
    }
  };

  const handleAddToCart = async (index) => {
    const newCartCounts = [...cartCounts];
    newCartCounts[index] += 1;
    setCartCounts(newCartCounts);

    try {
      // Check if user data is available
      if (userData) {
        const response = await fetch('http://localhost:3002/order', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            username: userData.username,
            address: userData.address,
            coffee: newCartCounts[0],
            pasta: newCartCounts[1],
            rosemilk: newCartCounts[2],
          }),
        });

        if (!response.ok) {
          console.error('Order failed:', response.statusText);
          // Optionally, you can handle failure here (e.g., show an error message)
          // If needed, you can rollback the local state update
          newCartCounts[index] -= 1;
          setCartCounts(newCartCounts);
        }
      } else {
        console.error('User data not available');
      }
    } catch (error) {
      console.error('Order failed:', error);
      newCartCounts[index] -= 1;
      setCartCounts(newCartCounts);
    }
  };

  const placeOrder = async () => {
    try {
      // Check if user data is available
      if (userData) {
        const response = await fetch('http://localhost:3002/order', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            username: userData.username,
            address: userData.address,
            coffee: cartCounts[0],
            pasta: cartCounts[1],
            rosemilk: cartCounts[2],
          }),
        });

