import React, { useState } from 'react';
import axios from 'axios';
import Lnav from '../components/lnav';
import './login.css';
import { Link } from 'react-router-dom';

export default function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const handleLogin = async (e) => {
      e.preventDefault();
      console.log({email,password})

        try {
            const response = await axios.post('http://localhost:3002/login', {
                email:email,
                password:password,
            });

            console.log(response.data.message);
        } catch (error) {
            if (error.response) {
                console.error('Server responded with error:', error.response.data.error);
                setError('Invalid email or password');
            } else {
                console.error('An unexpected error occurred:', error.message);
                setError('An unexpected error occurred');
            }
        }
    };

    return (
        <html>
            <body>
                <div>
                    <Lnav />
                    <div className='flex justify-center content-center mt-60'>
                        <div className='w-96 h-96 flex justify-center items-center bg-black rounded-2xl'>
                            <form>
                                <p className='text-white text-2xl mb-2'>Enter Email id</p>
                                <input
                                    type='email'
                                    onChange={(e) => setEmail(e.target.value)}
                                    className='bg-red-500 h-10 w-64 font-bold text-white text-center rounded-md'
                                    placeholder='mail id'
                                ></input>

                                <p className='text-white text-2xl mt-6 mb-2'>Enter Password</p>
                                <input
                                    type='password'
                                    onChange={(e) => setPassword(e.target.value)}
                                    className='bg-red-500 h-10 w-64 font-bold text-white text-center rounded-md'
                                    placeholder='password'
                                ></input>
                                <hr></hr>
                                {error && <p className='text-red-500 mt-2'>{error}</p>}
                                <button
                                    type='submit'
                                    className='text-white font-bold text-xl h-12 w-20 rounded-lg bg-red-500 hover:bg-red-400 hover:text-black mt-10 translate-x-20'
                                    onClick={handleLogin}
                                >
                                    Login
                                </button>

                                <p className='text-white flex justify-center mt-5 gap-2'>
                                    New user??<Link to='/signin' className='text-sky-400'>
                                        Create an account
                                    </Link>
                                </p>
                            </form>
                        </div>
                    </div>
                </div>
            </body>
        </html>
    );
}
