import React from 'react'
import Navi from '../components/anav'
import cc from '../images/cc.png'
import { useState } from 'react';
import plus from '../images/plus.png'
import minus from '../images/minus.png'
import pp from '../images/pp.jpg';
import rr from '../images/rr.jpg'
import './menu.css'

export default function Menu() {
  const [cartCounts, setCartCounts] = useState([
    { itemName: 'Coffee', count: 0 },
    { itemName: 'Pastas', count: 0 },
    { itemName: 'Rose Milk', count: 0 },
    { itemName: 'Shoes', count: 0 },
    { itemName: 'Shoes', count: 0 },
    { itemName: 'Shoes', count: 0 },
  ]);
  const [userData, setUserData] = useState({
    username: 'your_username_placeholder', 
    address: 'user_address_placeholder', 
  });

  const handleAddToCart = (index) => {
    const newCartCounts = [...cartCounts];
    newCartCounts[index].count += 1;
    setCartCounts(newCartCounts);
  };

  const handleRemoveFromCart = (index) => {
    if (cartCounts[index].count > 0) {
      const newCartCounts = [...cartCounts];
      newCartCounts[index].count -= 1;
      setCartCounts(newCartCounts);
    }
  };
  const handlePlaceOrder = async () => {
    const orderApiEndpoint = 'http://localhost:3002/order';

    try {
      const response = await fetch(orderApiEndpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: userData.username,
          items: cartCounts,
          address: userData.address,
          status: 'pending', // Set the default status or replace with your logic
        }),
      });

      if (response.ok) {
        console.log('Order placed successfully!');
        // Optionally, you can reset the cart counts or perform other actions
      } else {
        console.error('Failed to place order');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };



  return (
    <div>
      <Navi/>
      <form>
      <div>
      <div className='car flex justify-center items-center mt-10 gap-10 mb-10'>
        {cartCounts.map((item, index) => (
          <div key={index} className="card card-compact w-96 h-96 bg-red-200 shadow-2xl">
            <figure><img className='cc' src={cc} alt={item.itemName} /></figure>
            <div className="card-body">
              <h2 className="card-title">{item.itemName}</h2>
              <p>Feeling tired? Get Coffee!!</p>
              <div className="bu card-actions flex justify-end ">
                <button className='btn flex bg-red-500 hover:bg-red-500'>
                  <img src={minus} alt='plus' className='plus cursor-pointer' onClick={() => handleRemoveFromCart(index)} />
                  <p className='font-black'>{item.count}</p>
                  <img src={plus} alt='plus' className='minus cursor-pointer' onClick={() => handleAddToCart(index)} />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
          <div className="card card-compact w-96 h-96 bg-red-200 shadow-2xl">
            <figure><img className='pp' src={pp} alt="pp" /></figure>
            <div className="card-body">
              <h2 className="card-title">Pastas!</h2>
              <p>Feels hungry? Teste some yummy pastas</p>
              <div className="bu card-actions flex justify-end ">
                <button className='btn flex bg-red-500 hover:bg-red-500'>
                  <img src={minus} alt='plus' className='minus cursor-pointer' onClick={()=>handleRemoveFromCart(1)}/>
                  <p className='font-black'>{cartCounts[1]}</p>
                  <img src={plus} alt='plus' className='plus cursor-pointer' onClick={()=>handleAddToCart(1)}/>
                </button>
              </div>
            </div>
          </div>
          <div className="card card-compact w-96 h-96 bg-red-200 shadow-2xl">
            <figure><img src={rr} className='rr' alt="rr" /></figure>
            <div className="card-body">
              <h2 className="card-title">Rose Milk!</h2>
              <p>Get chilled with some Rose Milk!!</p>
              <div className="bu card-actions flex justify-end ">
                <button className='btn flex bg-red-500 hover:bg-red-500'>
                  <img src={minus} alt='plus' className='minus cursor-pointer' onClick={()=>handleRemoveFromCart(2)}/>
                  <p className='font-black'>{cartCounts[2]}</p>
